ymaps.ready(init);function init(){var myMap;if($('#map').data('coordinate')){coords=$('#map').data('coordinate').split(",");var coord=[];coord[0]=parseFloat(coords[0]);coord[1]=parseFloat(coords[1]);myMap=new ymaps.Map('map',{center:coord,zoom:15,controls:['zoomControl','fullscreenControl']},{suppressMapOpenBlock:true});myMap.behaviors.disable('scrollZoom');myMap.geoObjects.add(new ymaps.Placemark(coord,{},{iconLayout:'default#image',iconImageHref:'/images/placemark.png',iconImageSize:[50,50],iconImageOffset:[-25,-50]}));}}